import React, { Component } from 'react';
import Web3 from 'web3';

export const initialState = {
    value: 1, 
    blockValue: 12
  };

export const increment = counter => by => {
    counter.setState({ value: counter.state.value + by });
  };

export const decrement = counter => by => {
    counter.setState({ value: counter.state.value - by });
  };
