import React, { useCallback,useState, useEffect, useRef } from 'react';

import { useCounter, useBlock } from './entities';
// import { initialState, blockNo } from './counter';
// var web3 = new Web3(new Web3.providers.HttpProvider('http://localhost:8545'));
// var hello =0;

function useInterval(callback, delay) {
    const savedCallback = useRef();

    // Remember the latest callback.
    useEffect(() => {
      savedCallback.current = callback;
    }, [callback]);

    // Set up the interval.
    useEffect(() => {
      function tick() {
        savedCallback.current();
      }
      if (delay !== null) {
        let id = setInterval(tick, delay);
        return () => clearInterval(id);
      }
    }, [delay]);
  }

export const CounterView = () => {
    // let [count, setCount] = useState(0);

   useInterval(() => {
    // Your custom logic here
    // setCount(count + 1);
    increment(1);
    intia;
    }, 1000);

  const [block, { blockNo}] = useBlock();
  const [counter, { increment, decrement }] = useCounter();

  const handleClickIncrement = useCallback(() => increment(1), []);
  const handleClickDecrement = useCallback(() => decrement(1), []);

//   this.interval2 = setInterval( () => [this.getBlock(), this.getBlocks()], 10000);

  return (

    <>
        <div><h2>Dynamic Count</h2></div>
      <div>{counter.value}</div>
      <div>{block.blockValue}</div>
      <button onClick={handleClickIncrement}>Increment</button>
      <button onClick={handleClickDecrement}>Decrement</button>
    </>
  )
};

